var rein
function in_resize(){
	rein=setTimeout(function(){
		$(".top_slide li").each(function(index,el){
			if(winH/winW>900/1600){
				$(this).find("img").css({height:winH,width:winH*(1600/900),marginTop:-winH/2,marginLeft:-(winH*(1600/900))/2});
			}else{
				$(this).find("img").css({height:winW*(900/1600),width:winW,marginTop:-(winW*(900/1600))/2,marginLeft:-winW/2});
			}
		});
	},200);
}
function clock(){
	var tdiv='<div class="clock"><span class="Day"></span> / <span class="Month"></span> / <span class="Year"></span>&nbsp;<span class="h"></span> <span class="c"> : </span>&nbsp;<span class="m"></span>&nbsp;<span class="c"> : </span>&nbsp;<span class="s"></span></div><div class="line_l"></div><div class="line_r"></div>';
	$(".top_slide li").each(function(index,el){$(this).append(tdiv);});
	$(".top_slide_img").each(function(index,el){
		var st=$(this).attr("class");
		for(var i=tz.length;i--;){
			if(st.indexOf(tz[i][0])!=-1){
				$(this).find(".clock").attr("data-tz",tz[i][1]);
				$(this).find(".clock").attr("data-taxt",tz[i][2]);
			}
		}
	});
	update_watch();
}
var tm=[];
String.prototype.right=function(n){return this.substr(this.length-n,n);};
Date.prototype.setTimezone=function(tz){var utc=new Date(this.getTime()+this.getTimezoneOffset()*60*1000);return new Date(utc.getTime()+tz/100*60*60*1000);};
var mon=['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
var tz=[
	["slide-01","+0700"," Ampera Bridge, Palembang, WIB "],
["slide-02","+0800"," Pura Uluwatu, Bali, WITA "],
	["slide-03","+0900"," Asana Biak, Papua, WIT "],
	["slide-04","+0700"," Toba Lake, North Sumatra, WIB "],
	["slide-05","+0800"," Tanjung Aan Beach, Lombok, WITA "],
	["slide-06","+0900"," Lorentz National Park, Papua, WIT "],
];
var Timezone="+0700";
function update_d(){
	var now=new Date();
	$(".clock").each(function(index,el){
		var dst=now.setTimezone($(this).attr("data-tz"));
		tm[5]=dst.getFullYear();tm[4]=mon[dst.getMonth()];tm[3]=(dst.getDate());tm[2]=("0"+dst.getHours()).right(2);tm[1]=("0"+dst.getMinutes()).right(2);tm[0]=("0"+dst.getSeconds()).right(2);
		$(this).find(".s").text(tm[0]);$(this).find(".m").text(tm[1]);$(this).find(".h").text(tm[2]);$(this).find(".Day").text(tm[3]);$(this).find(".Month").text(tm[4]);$(this).find(".Year").text(tm[5]);
	});
}
function update_watch(){update_d();setTimeout('update_watch()',999);}
